// reducer related to cart state;
const reducer=(state,action)=>{
    switch (action.type){
        case "ADD_TO_CART":{
            return [...state,action.payload];
        }
        case "REMOVE_FROM_CART":{
            const cartAfterItemRemoval=state.filter()
            return [...state,action.payload];
        }
    }
}
